% Compute average edge segment length for AADP model (derivative)
function dldr = mm10_dlsdr_aadp(props, np1, n, tau, rho, alpha, beta, es)

% Set up H matrix
Hab_se = zeros(12); % Table 2
% Hab_ss = eye(12); % Table 2
% [~,Hab_ss] = mm10_aadp_GH(1);
Hab_ss = [[0,0,0,0.0141421356237310,0.117851130197758,0.113137084989848,0.0141421356237310,0.113137084989848,0.117851130197758,0.0282842712474619,0.0565685424949238,0.0565685424949238;0,0,0,0.117851130197758,0.0141421356237309,0.113137084989848,0.0565685424949238,0.0471404520791032,0.0565685424949238,0.113137084989848,0.0141421356237310,0.117851130197758;0,0,0,0.0565685424949238,0.0565685424949238,0.0471404520791032,0.117851130197758,0.113137084989848,0.0141421356237309,0.113137084989848,0.117851130197758,0.0235702260395516;0.0141421356237310,0.117851130197758,0.113137084989848,0,0,0,0.0282842712474619,0.0565685424949238,0.0565685424949238,0.0141421356237310,0.113137084989848,0.117851130197758;0.117851130197758,0.0141421356237309,0.113137084989848,0,0,0,0.113137084989848,0.0141421356237310,0.117851130197758,0.0565685424949238,0.0471404520791032,0.0565685424949238;0.0565685424949238,0.0565685424949238,0.0471404520791032,0,0,0,0.113137084989848,0.117851130197758,0.0235702260395516,0.117851130197758,0.113137084989848,0.0141421356237309;0.0141421356237310,0.113137084989848,0.117851130197758,0.0282842712474619,0.0565685424949238,0.0565685424949238,0,0,0,0.0141421356237310,0.117851130197758,0.113137084989848;0.0565685424949238,0.0471404520791032,0.0565685424949238,0.113137084989848,0.0141421356237310,0.117851130197758,0,0,0,0.117851130197758,0.0141421356237309,0.113137084989848;0.117851130197758,0.113137084989848,0.0141421356237309,0.113137084989848,0.117851130197758,0.0235702260395516,0,0,0,0.0565685424949238,0.0565685424949238,0.0471404520791032;0.0282842712474619,0.0565685424949238,0.0565685424949238,0.0141421356237310,0.113137084989848,0.117851130197758,0.0141421356237310,0.117851130197758,0.113137084989848,0,0,0;0.113137084989848,0.0141421356237310,0.117851130197758,0.0565685424949238,0.0471404520791032,0.0565685424949238,0.117851130197758,0.0141421356237309,0.113137084989848,0,0,0;0.113137084989848,0.117851130197758,0.0235702260395516,0.117851130197758,0.11313708498