function dssdr = mm10_dssdr_aadp(props, np1, n, rho, alpha, beta, es)

% Set some properties
mu = props.mu_0;
b = props.burgers;

% Set up G matrix
% Gab_ss = eye(12); % Table 2?
Gab_se = zeros(12); % Table 2?
% Gab_ss = mm10_aadp_GH(1);
Gab_ss = [0.100000000000000,0.220000000000000,0.220000000000000,0.0754247233265651,0.212132034355964,0.358267435801184,0.0754247233265651,0.358267435801184,0.212132034355964,0.150849446653130,0.179133717900592,0.179133717900592;0.220000000000000,0.100000000000000,0.220000000000000,0.212132034355964,0.0754247233265651,0.358267435801184,0.179133717900592,0.282842712474619,0.179133717900592,0.358267435801184,0.0754247233265651,0.212132034355964;0.220000000000000,0.220000000000000,0.100000000000000,0.179133717900592,0.179133717900592,0.282842712474619,0.212132034355964,0.358267435801184,0.0754247233265651,0.358267435801184,0.212132034355964,0.141421356237310;0.0754247233265651,0.212132034355964,0.358267435801184,0.100000000000000,0.220000000000000,0.220000000000000,0.150849446653130,0.179133717900592,0.179133717900592,0.0754247233265651,0.358267435801184,0.212132034355964;0.212132034355964,0.0754247233265651,0.358267435801184,0.220000000000000,0.100000000000000,0.220000000000000,0.358267435801184,0.0754247233265651,0.212132034355964,0.179133717900592,0.282842712474619,0.179133717900592;0.179133717900592,0.179133717900592,0.282842712474619,0.220000000000000,0.220000000000000,0.100000000000000,0.358267435801184,0.212132034355964,0.141421356237310,0.212132034355964,0.358267435801184,0.0754247233265651;0.0754247233265651,0.358267435801184,0.212132034355964,0.150849446653130,0.179133717900592,0.179133717900592,0.100000000000000,0.220000000000000,0.220000000000000,0.0754247233265651,0.212132034355964,0.358267435801184;0.179133717900592,0.282842712474619,0.179133717900592,0.358267435801184,0.0754247233265651,0.212132034355964,0.220000000000000,0.100000000000000,0.220000000000000,0.212132034355964,0.0754247233265651,0.358267435801184;0.212132034355964,0.358267435801184,0.0754247233265651,0.358267435801184,0.212132034355964,0.141421356237310,0.220000000000000,0.220000000000000,0.100000000000000,0.179133717900592,0.179133717900592,0.282842712474619;0.150849446653130,0.179133717900592,0.179133717900592,0.0754247233265651,0.358267435801184,0.212132034355964,0.0754247233265651,0.212132034355964,0.358267435801184,0.100000000000000,0.220000000000000,0.220000000000000;0.358267435801184,0.0754247233265651,0.212132034355964,0.179133717900592,0.282842712474619,0.179133717900592,0.212132034355964,0.0754247233265651,0.358267435801184,0.220000000000000,0.100000000000000,0.220000000000000;0.358267435801184,0.212132034355964,0.141421356237310,0.212132034355964,0.358267435801184,0.0754247233265651,0.179133717900592,0.179133717900592,0.282842712474619,0.220000000000000,0.220000000000000,0.100000000000000];


if es == 1 % beta is edge
    Gab = Gab_se(alpha,beta);
else % beta is screw
    Gab = Gab_ss(alpha,beta);
end

% Evaluate the equation
dssdr = 1.d0/2.d0 * mu * b / sqrt(Gab_se(alpha,:)*reshape(rho(1:12),12,1) + ...
                     Gab_ss(alpha,:)*reshape(rho(13:24),12,1)) ...
                     * Gab; %(A.14)